        if (hasMilestone('mario', 3) && player.super_hammer.buyables[13].lt(limitBoxtranscender)) player.super_hammer.buyables[13] = limitBoxtranscender

        let c = d("e8.1e1919")
        if (hasSEendlessUpgrade(43)) c = c.pow(upgradeEffect('s_expert', 43))
